const NextI18Next = require("next-i18next").default;

const NextI18NextInstance = new NextI18Next({
	defaultLanguage: "en",
	otherLanguages: ["hu"]
});

const { appWithTranslation, withTranslation } = NextI18NextInstance;

module.exports = NextI18NextInstance;
